import React, {
  useState,
  useEffect
} from "react";
import ReactDOM from "react-dom";
import 'bootstrap/dist/css/bootstrap.css';
import "./index.css";
import 'jquery/dist/jquery.js';
import 'popper.js/dist/umd/popper.js';
import 'bootstrap/dist/js/bootstrap.js';

let pageData = []

const setPreviewHtml = html => {
document.getElementById("preview").innerHTML = html;
};


const AdReport = ({adid, spend, cpl, cac, adpreview}) => {
	return (
    <>

    <div className ="container">
      <h2># {adid}</h2>
      <p>Spend: {spend}</p>
			<p>CPL: {cpl}</p>
			<p>CAC: {cac}</p>

      <div id="preview"> {adpreview} </div>


  </div>

  </>

	)
}

function AdData() {

const [data, setData] = useState(null);
  useEffect(() => {
   fetch(`https://jf-na-cdn.justfab.com/image/site/en_US/dm/acquisition/feed/justfab/topconvert/jsonTest.json`)

      .then(res => res.json())
      // .then(json => console.log(json))
      .then(setData)
      .catch(console.error);
  }, []);

  if (data) {

     pageData = data

    return (

      <>

      <div>

      {pageData.map(
           (adreport, i) => <AdReport key={i} adid={i+1} spend={pageData[i].spend} cpl={pageData[i].cpl} cac={pageData[i].cac}  adpreview={setPreviewHtml(pageData[i].adpreview.data?.length ? pageData[i].adpreview.data[i].body : content.message)} />

      )}

      </div>

      </>

    );
  }
  return null;
}


function TopAdsPage() {

return <AdData/>;
}

ReactDOM.render(
  <TopAdsPage/>,
  document.getElementById("root")
);
