import React, {
  useState,
  useEffect
} from "react";
import ReactDOM from "react-dom";
import 'bootstrap/dist/css/bootstrap.css';
import "./css/index.css";
import 'jquery/dist/jquery.js';
import 'popper.js/dist/umd/popper.js';
import 'bootstrap/dist/js/bootstrap.js';


let pageData = []


const AdReport = ({adid, spend, cpl, cac, adpreview}) => {
	return (
		<section>
      <p># {adid} </p>
      <p>Spend: {spend}</p>
			<p>CPL: {cpl}</p>
			<p>CAC: {cac}</p>
      <h1>Preview:</h1>
      <iframe title="Ad Preview" src={adpreview}>
      </iframe>
		</section>
	)
}

function AdData() {

const [data, setData] = useState(null);
  useEffect(() => {
   fetch(`https://jf-na-cdn.justfab.com/image/site/en_US/dm/acquisition/feed/justfab/topconvert/jsonTest.json`)

      .then(res => res.json())
      // .then(json => console.log(json))
      .then(setData)
      .catch(console.error);
  }, []);

  if (data) {

     pageData = data

    return (

      <div>

      {pageData.map(
           (adreport, i) => <AdReport key={i} adid={i+1} spend={pageData[i].spend} cpl={pageData[i].cpl} cac={pageData[i].cac}  adpreview={pageData[i].adpreview} />
      )}

      </div>
    );
  }
  return null;
}


function TopAdsPage() {
  return <AdData/>;
}

ReactDOM.render(
  <TopAdsPage/>,
  document.getElementById("root")
);
